te(); $stmt->close(); $this->dbCache->setField("rawstatus", $newStatus); return true; } public function hasOpenedOnlineId() { if (! $this->created || $this->deleted) throw new Exception("There is no user assigned"); return $this->hasOpenedOnlineId; } public function getId() { if (! $this->created || $this->deleted) throw new Exception("There is no user assigned"); return $this->id; } public function getEmail() { if (! $this->created || $this->deleted) throw new Exception("There is no user assigned"); if ($this->dbCache->inCache("email")) return $this->dbCache->getField("email"); $dbCon = DatabaseConnection::getDatabaseConnection(); $table = ($this->getEmailactivated())? "users" : "registrations"; $stmt = $dbCon->prepare("SELECT `email` FROM `{dbpre}$table` WHERE `id`=?"); $returnValue = false; $stmt->bind_param("i", $this->id); $stmt->execute(); $stmt->bind_result($email); if ($stmt->fetch()) { $returnValue = $email; $this->dbCache->setField("email", $email); } $stmt->close(); return $returnValue; } public function setEmail($email) { if (! $this->created || $this->deleted) throw new Exception("There is no user assigned"); $dbCon = DatabaseConnection::getDatabaseConnection(); $table = ($this->getEmailactivated())? "users" : "registrations"; $stmt = $dbCon->prepare("UPDATE `{dbpre}$table` SET `email`=? WHERE `id`=?"); $stmt->bind_param("si", $email, $this->id); $stmt->execute(); $this->dbCache->setField("email", $email); $stmt->close(); } public function getUsername() { if (! $this->created || $this->deleted) throw new Exception("There is no user assigned"); if ($this->dbCache->inCache("username")) return $this->dbCache->getField("username"); $dbCon = DatabaseConnection::getDatabaseConnection(); $table = ($this->getEmailactivated())? "users" : "registrations"; $returnValue = false; $stmt = $dbCon->prepare("SELECT `username` FROM `{dbpre}$table` WHERE `id`=?"); $stmt->bind_param("i", $this->id); $stmt->execute(); $stmt->bind_result($username); if ($stmt->fetch()) { $re