ivated())? "users" : "registrations"; $stmt = $dbCon->prepare("UPDATE `{dbpre}$table` SET `password`=? WHERE `id`=?"); $stmt->bind_param("si", $encodedPassword, $this->id); $stmt->execute(); $stmt->close(); $this->dbCache->setField("checkpassword_$password", true); return true; } public function checkPassword($password) { if (! $this->created || $this->deleted) throw new Exception("There is no user assigned"); if ($this->dbCache->inCache("checkpassword_$password")) return $this->dbCache->getField("checkpassword_$password"); if (! (isset($password) && strlen($password) > 0)) return false; $dbCon = DatabaseConnection::getDatabaseConnection(); $table = ($this->getEmailactivated())? "users" : "registrations"; $stmt = $dbCon->prepare("SELECT `password` FROM `{dbpre}$table` WHERE id=? LIMIT 1"); $stmt->bind_param("i", $this->id); $stmt->execute(); $stmt->bind_result($realPassword); $stmt->fetch(); $stmt->close(); $encodedGivenPassword = self::encodePassword($password, $realPassword); $this->dbCache->setField("checkpassword_$password", $isCorrect = ($encodedGivenPassword === $realPassword)); return $isCorrect; } public function getGroups($limit=null, $skip=null) { if (! $this->created || $this->deleted || $this->registeredOnly) throw new Exception("There is no user assigned"); $limitCommand = ""; if (is_int($limit)) $limitCommand = is_int($skip)? " LIMIT $skip, $limit" : " LIMIT $limit"; elseif (is_int($skip)) throw new Exception("Cannot skip without limiting"); $groupArray = array(); $groupIds = array(); $dbCon = DatabaseConnection::getDatabaseConnection(); $stmt = $dbCon->prepare("SELECT `groupid` FROM `{dbpre}user_groups` WHERE `userid`=?$limitCommand"); $stmt->bind_param("i", $this->id); $stmt->execute(); $stmt->bind_result($groupId); while ($stmt->fetch()) $groupIds[] = $groupId; $stmt->close(); foreach ($groupIds as $groupId) { $group = new Group(); $group->openWithId($groupId); $groupArray[] = $group; } return $groupArray; } public function inGroup($groupId) { if (!