); return $returnValue; } public function saveSessionVar($title, $value) { if (! $this->created || $this->deleted || ! $this->hasOpenedOnlineId) throw new Exception("There is no user assigned"); $onlineId = $this->getOnlineId(); $dbCon = DatabaseConnection::getDatabaseConnection(); $searchStmt = $dbCon->prepare("SELECT `id` FROM `{dbpre}sessionsvars` WHERE `title`=? AND `onlineid`=? LIMIT 1"); $searchStmt->bind_param("si", $title, $onlineId); $searchStmt->execute(); $searchStmt->store_result(); $searchStmt->bind_result($sessionVarid); if ($searchStmt->num_rows > 0) { $searchStmt->fetch(); $searchStmt->close(); $updateStmt = $dbCon->prepare("UPDATE `{dbpre}sessionsvars` SET `value`=? WHERE `id`=? LIMIT 1"); $serialziedVar = serialize($value); $updateStmt->bind_param("si", $serialziedVar, $sessionVarid); $updateStmt->execute(); $updateStmt->close(); } else { $searchStmt->close(); $insertStmt = $dbCon->prepare("INSERT INTO `{dbpre}sessionsvars` (`onlineid`, `title`, `value`) VALUES (?, ?, ?)"); $serialziedVar = serialize($value); $insertStmt->bind_param("iss", $onlineId, $title, $serialziedVar); $insertStmt->execute(); $insertStmt->close(); } $this->dbCache->setField("sessionvar_$title", $value); } public function getSessionVar($title) { if (! $this->created || $this->deleted || ! $this->hasOpenedOnlineId) throw new Exception("There is no user assigned"); if ($this->dbCache->inCache("sessionvar_$title")) return $this->dbCache->getField("sessionvar_$title"); $onlineId = $this->getOnlineId(); $returnValue = null; $dbCon = DatabaseConnection::getDatabaseConnection(); $stmt = $dbCon->prepare("SELECT `value` FROM `{dbpre}sessionsvars` WHERE `title`=? AND `onlineid`=? LIMIT 1"); $stmt->bind_param("si", $title, $onlineId); $stmt->execute(); $stmt->store_result(); $stmt->bind_result($value); if ($stmt->num_rows > 0) { $stmt->fetch(); $returnValue = unserialize($value); } $stmt->close(); $this->dbCache->setField("sessionvar_$title", $returnValue); return $returnValue; } public function deleteUser() { if (! $this->created || $this->deleted) throw new Exception("There is no user assigned"); $dbCon = DatabaseConnection::getDatabaseConnection(); $table = ($this->getEmailactivated())? "users" : "registrations"; $delUserStmt = $dbCon->prepare("DELETE FROM `{dbpre}$table` WHERE `id`=? LIMIT 1"); $delUserStmt->bind_param("i", $this->id); $delUserStmt->execute(); $delUserStmt->close(); $delGroupConnectionsStmt = $dbCon->prepare("DELETE FROM `{dbpre}user_groups` WHERE `userid`=? LIMIT 1"); $delGroupConnectionsStmt->bind_param("i", $this->id); $delGroupConnectionsStmt->execute(); $delGroupConnectionsStmt->close(); $delPermissionConnectionsStmt = $dbCon->prepare("DELETE FROM `{dbpre}user_permissions` WHERE `userid`=? LIMIT 1"); $delPermissionConnectionsStmt->bind_param("i", $this->id); $delPermissionConnectionsStmt->execute(); $delPermissionConnectionsStmt->close(); $this->deleted = true; return true; } public function activateEmail($activationCode) { if (! $this->created || $this->deleted) throw new Exception("There is no user assigned"); if ($this->getEmailactivated()) return self::ACTIVATEEMAIL_ALREADYACTIVATED; $settings = new UserLibrarySettings(); $dbCon = DatabaseConnection::getDatabaseConnection(); $checkStmt = $dbCon->prepare("SELECT `login`, `username`, `password`, `email`, `status`, `activationcode`, `secure_cookie_string`, `registerdate` FROM `{dbpre}registrations` WHERE `id`=? LIMIT 1"); $checkStmt->bind_param("i", $this->id); $checkStmt->execute(); $checkStmt->bind_result($db_loginname, $db_username, $db_password, $db_email, $db_status, $db_activationCode, $db_cookieString, $db_registerDate); $checkStmt->fetch(); $checkStmt->close(); if ($activationCode != $db_activationCode) return self::ACTIVATEEMAIL_ACTIVATIONCODEWRONG; else { $activateStmt = $dbCon->prepare("INSERT INTO `{dbpre}users` (`id`, `login`, `username`, `password`, `email`, `status`, `secure_cookie_string`, `registerdate`) VALUES (?, ?, ?, ?, ?, ?, ?, ?)"); $activateStmt->b